<?php\n  $headers = file('headers.txt', FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n\n  foreach ($headers as $header) {\n      list($http_code, $url) = explode(' ', trim($header), 2);\n      \n      // Database connection and insertion logic here\n      $mysqli = new mysqli("localhost", "user", "pass", "database");\n      if (!$mysqli->connect_error) {\n          $stmt = $mysqli->prepare("INSERT INTO headers (url, http_code) VALUES (?, ?)");\n          $stmt->bind_param("si", $url, $http_code);\n          $stmt->execute();\n          $stmt->close();\n      }\n      $mysqli->close();\n  }\n  ?>