static function getAll($conditions = ' ')\n{\n    $db = null;\n    $results = null;\n    $records = array();\n    // Add a conditional order based on points (votes)\n    $query = "SELECT id, created, modified, username, url, title, description, points, ranking \n              FROM articles \n              WHERE 1=1\n              ORDER BY CASE WHEN votes > 1 THEN ranking ELSE -points END DESC,\n                       CASE WHEN votes > 1 THEN NULL ELSE created END DESC\n              $conditions";\n    try {\n        $db = parent::getConnection(); \n        $results = parent::execSql($query);\n\n        while($row = $results->fetch_assoc()) {\n            // Your existing code to populate articles\n            $article = new Article(...);\n            $records[] = $article;\n        }\n        parent::closeConnection($db);\n    } catch(Exception $e) {\n        throw $e;\n    }\n\n    return $records;\n}