function cleanTags($tagsInput) {\n    // Split the input string by commas\n    $tagsArray = explode(',', $tagsInput);\n    \n    // Trim whitespace from each tag and filter out empty tags\n    $cleanTags = array_filter(array_map('trim', $tagsArray), function($tag) {\n        return !empty($tag); // Keep only non-empty tags\n    });\n\n    return $cleanTags;\n}\n\n// Example usage\n$inputTags = "battleship, strategy, games,, sea war, , , ,";\n$tagsToInsert = cleanTags($inputTags);\n\nprint_r($tagsToInsert);