<?php\n\nfunction experience_query($id) {\n    // Assuming $conn is your MySQLi connection object\n    global $conn;\n\n    // Prepare the statement\n    if ($stmt = $conn->prepare("SELECT * FROM table WHERE id = ?")) {\n        // Bind the variable to the prepared statement as a parameter\n        $stmt->bind_param("i", $id);\n\n        // Execute query\n        $stmt->execute();\n\n        // Return the result set\n        return $stmt;\n    } else {\n        echo "Error: " . $conn->error;\n        return null;\n    }\n}\n\n// Usage example:\n$conn = new mysqli('host', 'user', 'password', 'database');\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$q = experience_query(1001);\n$result = $q->get_result();\nwhile ($list = $result->fetch_assoc()) {\n    extract($list);\n    echo $name;\n}\n\n// Don't forget to close the statement and connection\n$q->close();\n$conn->close();\n?>