try {\n    $event = $gcal->newEventEntry();\n    $event->title = $gcal->newTitle($title);\n    $event->content = $gcal->newContent($desc);\n    $when = $gcal->newWhen();\n    $when->startTime = $date;\n    $when->endTime = $date;\n    $event->when = array($when);\n    \n    // Insert the event\n    $insertedEvent = $gcal->insertEvent($event);\n\n    // Get and store the event ID or edit link\n    $eventId = $insertedEvent->id->text;\n    $editUrl = $insertedEvent->getEditLink()->href;\n\n    echo "Event ID: " . $eventId . "\n";\n    echo "Edit URL: " . $editUrl . "\n";\n\n} catch (Zend_Gdata_App_Exception $e) {\n    echo "Error: Unable to add event to Google Calendar" . $e->getResponse();\n}