<?php\n// Read the entire input stream into a string\n$imageData = file_get_contents('php://input');\n\nif ($imageData === FALSE) {\n    // Handle error if reading from input stream failed\n    die('Failed to read input data');\n}\n\n// Generate a unique filename for the image (e.g., using timestamp)\n$filename = 'images/' . uniqid() . '.jpg';\n\n// Write the received byte array to a file\n$result = file_put_contents($filename, $imageData);\n\nif ($result === FALSE) {\n    // Handle error if writing the file failed\n    die('Failed to write image data');\n}\n\necho "Image saved successfully as $filename";\n?>