// Step 2: Handle Authorize Response\n   if (isset($_GET['code'])) {\n       $app_id = 'YOUR_APP_ID';\n       $app_secret = 'YOUR_APP_SECRET';\n       $redirect_uri = "http://domain.com/facebook/auth_response/";\n       $code = $_GET["code"];\n\n       $token_url = "https://graph.facebook.com/v15.0/oauth/access_token?" .\n                    "client_id=" . $app_id .\n                    "&redirect_uri=" . urlencode($redirect_uri) .\n                    "&client_secret=" . $app_secret .\n                    "&code=" . $code;\n       $response = file_get_contents($token_url);\n       $params = null;\n       parse_str($response, $params);\n\n       // Save the token to a session or database\n       $_SESSION['access_token'] = $params['access_token'];\n       header("Location: " . $canvas_page_url);\n       exit;\n   }