<?php\n// Check if the file was uploaded successfully\nif (isset($_FILES['file']) && $_FILES['file']['error'] === UPLOAD_ERR_OK) {\n    $tmpName = $_FILES['file']['tmp_name'];\n    \n    // Initialize cURL session\n    $ch = curl_init();\n    \n    // Prepare POST fields\n    $postFields = [\n        'file' => new CURLFile($tmpName, $_FILES['file']['type'], $_FILES['file']['name'])\n    ];\n    \n    // Set the URL and other options for cURL\n    curl_setopt($ch, CURLOPT_URL, "http://serverB.com/upload.php");\n    curl_setopt($ch, CURLOPT_POST, true);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, $postFields);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Execute the cURL session\n    $response = curl_exec($ch);\n    \n    // Check for errors in the cURL execution\n    if ($response === false) {\n        echo 'cURL Error: ' . curl_error($ch);\n    } else {\n        echo 'File uploaded successfully to Server B. Response from Server B:' . $response;\n    }\n    \n    // Close cURL session\n    curl_close($ch);\n} else {\n    echo 'Error uploading file to Server A.';\n}\n?>