require 'vendor/autoload.php'; // Assuming composer is used to install AWS SDK\n\nuse Aws\S3\S3Client;\nuse Aws\Exception\AwsException;\n\n$s3 = new S3Client([\n    'version' => 'latest',\n    'region'  => 'us-east-1', // Change according to your bucket region\n    'credentials' => [\n        'key'    => 'YOUR_KEY',\n        'secret' => 'YOUR_SECRET'\n    ]\n]);\n\n$bucketName = "your-bucket-name";\n$key = "path/to/your/video.flv"; // S3 path to the file\n\ntry {\n    $cmd = $s3->getCommand('GetObject', [\n        'Bucket' => $bucketName,\n        'Key'    => $key\n    ]);\n    \n    $request = $s3->createPresignedRequest($cmd, '+20 minutes'); // URL valid for 20 mins\n    \n    echo (string) $request->getUri() . PHP_EOL;\n} catch (AwsException $e) {\n    echo "Error: " . $e->getMessage();\n}