// Fetch avatar URL from database based on user ID\nfunction getUserAvatar($user_id) {\n    // Assume $db is already set up as the database connection\n    $stmt = $db->prepare("SELECT avatar_url FROM users WHERE id = ?");\n    $stmt->execute([$user_id]);\n    $result = $stmt->fetch();\n\n    // Use default avatar if user has no avatar set or file does not exist\n    $avatarUrl = isset($result['avatar_url']) && !empty($result['avatar_url']) \n                 ? $result['avatar_url'] \n                 : 'default_avatar.png';\n\n    return $avatarUrl;\n}