$myArray = [\n    ["time" => "12:15", "date" => "2009-03-24"],\n    ["time" => "12:10", "date" => "2009-03-23"],\n    ["time" => "12:00", "date" => "2009-03-24"],\n    ["time" => "11:30", "date" => "2009-03-24"]\n];\n\nusort($myArray, function ($a, $b) {\n    // Convert the time to a timestamp for comparison\n    $one = strtotime("2000-01-01 {$a['time']}");\n    $two = strtotime("2000-01-01 {$b['time']}");\n\n    // Round down to the nearest half-hour\n    $one -= ($one % 1800);\n    $two -= ($two % 1800);\n\n    // Compare by half-hour intervals first\n    if ($one == $two) {\n        // If times are equal, compare by date\n        return strcmp($a['date'], $b['date']);\n    }\n    return $one - $two;\n});\n\nprint_r($myArray);