// Hook into 'wp_footer' to ensure full page data is available\nadd_action('wp_footer', 'tui_cifp_process_content');\n\nfunction tui_cifp_process_content() {\n    // Check if we are not in the admin area and it's a single post view\n    if (!is_admin() && is_single()) {\n        $categories = get_the_category();\n        if (!empty($categories)) {\n            $categoryID = $categories[0]->cat_ID;\n            \n            // Replace [categoryID] with actual category ID in your script string\n            $tui_cifp_insertvalue = get_option('tui_cifp_insertvalue');\n            $tui_cifp_insertvalue = str_replace("[categoryID]", $categoryID, $tui_cifp_insertvalue);\n            \n            // Evaluate the PHP code if necessary (again, use eval cautiously)\n            ob_start();\n            // Use eval only if there's no safer option\n            eval("?>" . $tui_cifp_insertvalue . "<?php ");\n            $evaluated_content = ob_get_clean();\n\n            // Output or modify the page content as needed\n            echo $evaluated_content;\n        }\n    }\n}