<?php\n\nfunction convertFractionsToHtmlEntities($text) {\n    // Define a mapping from fraction characters to their corresponding HTML entities\n    $fractionMap = [\n        '¼' => '&frac14;',\n        '½' => '&frac12;',\n        '¾' => '&frac34;',\n        // Add more mappings as needed\n    ];\n\n    // Replace each fraction character with its HTML entity equivalent\n    return str_replace(array_keys($fractionMap), array_values($fractionMap), $text);\n}\n\n// Example usage\n$textWithFractions = "We've got some fraction information stored in the database, e.g. ¾ ½";\n$safeHtmlText = convertFractionsToHtmlEntities($textWithFractions);\n\necho $safeHtmlText; // Outputs: We've got some fraction information stored in the database, e.g. &frac34; &frac12;\n\n?>