<?php\n// Database configuration\n$dbHost = 'localhost';\n$dbName = 'your_database_name';\n$dbUser = 'your_username';\n$dbPass = 'your_password';\n\ntry {\n    // Create new PDO connection\n    $pdo = new PDO("mysql:host=$dbHost;dbname=$dbName;charset=utf8", $dbUser, $dbPass);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Email message details\n    $email = 'example@example.com';\n    $Name = 'Test Name';\n    $message ="<html><body> <table><tr><td>HELLO</td></tr></table></body></html>";\n    $subject = "GoodNoon: ".$Name;\n\n    // Setup PHPMailer\n    require 'PHPMailer.php'; // Adjust the path to where your PHPMailer is located\n    $mail = new PHPMailer();\n    $mail->From = $email;\n    $mail->FromName = $Name;\n    $mail->CharSet = "utf-8";\n    $mail->Subject = $subject;\n    $mail->IsHTML(true);\n    $mail->AltBody = "To view the message, please use an HTML compatible email viewer";\n    $mail->MsgHTML($message);\n    $mail->AddAddress("recipient@example.com", "Recipient Name");\n\n    // Send email\n    if(!$mail->Send()) {\n        echo "Email Not sent: " . $mail->ErrorInfo;\n    } else {\n        echo "Email Sent";\n\n        // Insert data into database\n        $query = 'INSERT INTO emails (sender, recipient, subject, body) VALUES (:sender, :recipient, :subject, :body)';\n        $stmt = $pdo->prepare($query);\n        $stmt->execute([\n            ':sender' => $email,\n            ':recipient' => 'recipient@example.com',\n            ':subject' => $subject,\n            ':body' => $message,\n        ]);\n    }\n} catch(PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>