<?php\nclass Admin_TestController extends Zend_Controller_Action\n{\n    protected $_flashMessenger = null;\n\n    public function init()\n    {\n        // Ensure flash messenger is initialized correctly\n        $this->_flashMessenger = $this->_helper->getHelper('FlashMessenger');\n        $this->initView();\n    }\n\n    public function myAction()\n    {\n        // Add message to flash messenger\n        $this->_flashMessenger->addMessage('test message');\n\n        // Redirect to 'mynextrequest' action in the same controller\n        return $this->_helper->Redirector(\n            'mynextrequest',\n            'test', // Ensure this is the correct controller name if needed\n            'admin'\n        );\n    }\n\n    public function mynextrequestAction()\n    {\n        // Retrieve and display messages\n        $messages = $this->_flashMessenger->getMessages();\n        if (empty($messages)) {\n            echo "No messages found.";\n        } else {\n            zend_debug::dump($messages);\n        }\n        $this->render();\n    }\n}