// MySQLi procedural connection\n$mysqli = mysqli_connect("localhost", "user", "password", "database");\nif (!$mysqli) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// MySQL procedural connection (deprecated)\n$link = mysql_connect('localhost', 'user', 'password');\nif (!$link) {\n    die('Could not connect: ' . mysql_error());\n}\nmysql_select_db('database');\n\n// Using both connections\n$result_mysqli = mysqli_query($mysqli, "SELECT * FROM your_table");\n$result_mysql = mysql_query("SELECT * FROM your_table", $link);\n\n// Output results from MySQLi\nwhile ($row = mysqli_fetch_assoc($result_mysqli)) {\n    echo "MySQLi: " . $row['column_name'] . "<br>";\n}\n\n// Output results from MySQL\nwhile ($row = mysql_fetch_array($result_mysql)) {\n    echo "MySQL: " . $row['column_name'] . "<br>";\n}\n\n// Close connections\nmysqli_close($mysqli);\nmysql_close($link);