function formatPostcode($postcode) {\n    // Remove any existing spaces\n    $postcode = str_replace(' ', '', $postcode);\n    \n    // Define the pattern for matching and formatting the postcode\n    if (preg_match('/^(GIR0AA|[A-Z]{1,2}[0-9][0-9A-Z]?) ?([0-9][A-Z]{2})$/i', $postcode, $matches)) {\n        // If the postcode is already in correct format, return it\n        if (strpos($postcode, ' ') !== false) {\n            return $postcode;\n        }\n        // Otherwise, add the space correctly\n        return $matches[1] . ' ' . $matches[2];\n    } else {\n        // Return false or throw an error if the postcode is invalid\n        return false; // Or throw new Exception("Invalid postcode format");\n    }\n}\n\n// Example usage:\n$postcode = "W14SB";\n$formattedPostcode = formatPostcode($postcode);\nif ($formattedPostcode) {\n    echo $formattedPostcode; // Outputs: W1 4SB\n} else {\n    echo "Invalid postcode";\n}