<?php\n// Ensure the path to fcsh is correct and available in your environment's PATH\n$command = 'echo "compile /path/to/your/project" | /usr/bin/fcsh';\n\n// Execute the command\n$output = [];\nreturn_var = 0;\nexec($command, $output, $return_var);\n\n// Check output and return var\nif ($return_var === 0) {\n    echo "Compilation successful!";\n} else {\n    echo "Compilation failed with errors: " . implode("\n", $output);\n}\n?>