public function soapTest() {\n    echo "start <br />";\n    use_soap_error_handler(true);\n    \n    // Initialize client with WSDL URL\n    $client = new SoapClient("https://cwi.rezexchange.com:9991/Service.asmx?wsdl");\n    \n    // Prepare parameters as an associative array\n    $params = [\n        'PropertyCode' => 'YOUR_PROPERTY_CODE',\n        'DateFrom' => date('Y-m-d'), // Example format, adjust as necessary\n        'DateTo' => date('Y-m-d', strtotime('+1 day')), // Example: one day from now\n        'RatePlan' => 'YOUR_RATE_PLAN_CODE',\n        'RoomType' => 'YOUR_ROOM_TYPE_CODE',\n        'PromotionalRates' => false, // or true if you want promotional rates\n    ];\n\n    // Call the SOAP method with parameters\n    $result = $client->CwiRateDetails($params);\n\n    echo "<pre>";\n    print_r($result);\n    echo "</pre>";\n    \n    echo "<br /> end";\n}