/**\n * Returns a template after evaluating it\n * @param   string  $template   Template contents\n * @param   array   $variables  Associative array of variables to replace in the template\n * @return  string  Template output\n */\nfunction output_template($template, $variables = []) {\n    // Extract the variables into the local scope\n    extract($variables);\n    \n    // Use ob_start and ob_get_clean for safer eval usage\n    ob_start();\n    eval('?>' . $template);\n    return ob_get_clean();\n}\n\n// Usage example:\n$template_content = '<p>Hello, my name is <?php echo htmlspecialchars($name); ?>.</p>';\n$vars = ['name' => 'Ross'];\n\necho output_template($template_content, $vars);