function extractValidString($inputString, $forbiddenChars) {\n    // Convert the array of forbidden characters into a string for strpos\n    $forbiddenCharsString = implode('', $forbiddenChars);\n    \n    // Iterate through each character in the input string\n    for ($i = 0; $i < strlen($inputString); $i++) {\n        // Check if the current character is in the forbidden characters\n        if (strpos($forbiddenCharsString, $inputString[$i]) !== false) {\n            // Return the substring from start to the position before the forbidden character\n            return substr($inputString, 0, $i);\n        }\n    }\n    \n    // If no forbidden character is found, return the entire string\n    return $inputString;\n}\n\n$chars = array("a", "b", "c");\n$input = "log dog hat bat";\n$result = extractValidString($input, $chars);\n\necho $result; // Outputs: log dog h