$password = new Zend_Form_Element_Password('password', array(\n    'validators' => array(\n        'Alnum',\n        array('StringLength', array(6, 20))\n    ),\n    'filters'    => array('StringTrim'),\n    'label'      => 'Wachtwoord:'\n));\n$password->addFilter(new Ivo_Filters_Sha1Filter());\n\n$password2 = new Zend_Form_Element_Password('confirm_password', array(\n    'validators' => array(\n        'Alnum',\n        array('StringLength', array(6, 20)),\n        array('Identical', true, array('token' => 'password'))\n    ),\n    'filters'    => array('StringTrim'),\n    'required'   => true,\n    'label'      => 'Bevestig wachtwoord:'\n));\n// Note: Do not apply the Sha1Filter to password2 before validation.\n$password2->addValidator(new Zend_Validate_Identical(array(\n    'token' => 'password'\n)));\n\n$this->addElement($password);\n$this->addElement($password2);