function remove_img_dimensions($string) {\n    // Pattern to match width attribute with optional spaces and digits\n    $pattern_width = '/\s+width\s*=\s*"[\d]+"/i';\n    // Pattern to match height attribute with optional spaces and digits\n    $pattern_height = '/\s+height\s*=\s*"[\d]+"/i';\n\n    // Remove the width attribute\n    $string = preg_replace($pattern_width, '', $string);\n    // Remove the height attribute\n    $string = preg_replace($pattern_height, '', $string);\n\n    return $string;\n}