// app/View/Referrals/add.ctp\n\necho $this->Form->create('Referral');\necho $this->Form->input('firstName');\necho $this->Form->input('lastName');\necho $this->Form->input('emailAddress');\n\nfor ($i = 1; $i <= 10; $i++) {\n    echo '<div>';\n    echo $this->Form->input('Friend.' . $i . '.friendName', array('label' => 'Friend Name ' . $i));\n    echo $this->Form->input('Friend.' . $i . '.friendEmail', array('label' => 'Friend Email ' . $i));\n    echo '</div>';\n}\n\necho $this->Form->submit('Submit');\necho $this->Form->end();