function f_parse_csv($file, $longest, $delimiter)\n{\n  $mdarray = array();\n  $file    = fopen($file, "r");\n  while ($line = fgetcsv($file, $longest, $delimiter))\n  {\n    array_push($mdarray, $line);\n  }\n  fclose($file);\n  return $mdarray;\n}\n\nfunction sortMdArrayByDate(&$mdarray, $columnIndex)\n{\n    usort($mdarray, function($a, $b) use ($columnIndex) {\n        $dateA = strtotime($a[$columnIndex]);\n        $dateB = strtotime($b[$columnIndex]);\n        \n        // Sort in descending order (newest first)\n        return $dateB - $dateA;\n    });\n}\n\n// Example usage:\n$csvFile = 'yourfile.csv';\n$data = f_parse_csv($csvFile, 1000, ',');\n$sortColumn = 3; // Assuming the date column is at index 3\n\nsortMdArrayByDate($data, $sortColumn);\n\nprint_r($data);