public function rssAction()\n{\n    $this->_helper->layout()->disableLayout();\n    $this->_helper->viewRenderer->setNoRender(true);\n\n    // Generate the ETag and Last-Modified headers for caching\n    $feed = FeedUtil::getFeed($this->db);\n    $etag = md5($feed->saveXml());\n    $lastModified = new Zend_Date(filemtime(__FILE__), DATE_TIMESTAMP);\n\n    $response = $this->getResponse();\n    $request = $this->getRequest();\n\n    if ($request->getRawHeader('If-None-Match') === $etag || \n        ($request->getRawHeader('If-Modified-Since') && \n         strtotime($request->getRawHeader('If-Modified-Since')) >= $lastModified->getTimestamp())) {\n        // The feed has not been modified, send a 304 Not Modified response\n        $response->setHttpResponseCode(304);\n    } else {\n        // Set headers for caching control and ETag\n        $response->setHeader('ETag', $etag)\n                 ->setLastModified($lastModified);\n\n        // Send the feed\n        $response->header('Content-Type', 'application/rss+xml; charset=UTF-8');\n        $feed->send();\n    }\n}