function extractValues($location) {\n    // Using regex to ensure we're correctly capturing digits in the expected format\n    preg_match('/D(\d{2}):C(\d):E(\d):G(\d+)/', $location, $matches);\n    if (count($matches) !== 5) {\n        throw new Exception("Location string is not in the expected format.");\n    }\n\n    // Extract and convert to integers\n    $AB = intval($matches[1], 10); // D03 -> AB = 3\n    $CD = intval($matches[2] . $matches[4], 10); // C5:G7 -> CD = 57\n    $EF = intval($matches[3] . $matches[5], 10); // E1:D3 -> EF = 16\n\n    return ['AB' => $AB, 'CD' => $CD, 'EF' => $EF];\n}\n\n// Example usage\n$location = "D03:C5:E1:G7";\n$values = extractValues($location);