<?php\n// Sample date strings\n$date1 = '11/11/2002'; // d/m/Y\n$date2 = '11-11-2002'; // d-m-Y\n\n// Desired output format\n$dateFormat = 'm/d/Y';\n\n// Convert $date1 from d/m/Y to the desired format\n$formattedDate1 = DateTime::createFromFormat('d/m/Y', $date1)->format($dateFormat);\n\n// Convert $date2 from d-m-Y to the desired format\n$formattedDate2 = DateTime::createFromFormat('d-m-Y', $date2)->format($dateFormat);\n\necho "Formatted Date 1: " . $formattedDate1 . "\n";\necho "Formatted Date 2: " . $formattedDate2 . "\n";\n?>