<?php\n\n// Global function following camelCase convention\nfunction calculateTotalAmount($price, $quantity) {\n    return $price * $quantity;\n}\n\nclass Product {\n    // Property with camelCase\n    private $productPrice;\n\n    // Method with camelCase\n    public function setProductPrice($price) {\n        $this->productPrice = $price;\n    }\n\n    // Another method with camelCase\n    public function getProductPrice() {\n        return $this->productPrice;\n    }\n}\n\n$product = new Product();\n$product->setProductPrice(100);\n$total = calculateTotalAmount($product->getProductPrice(), 5);\n\necho "Total: $" . $total; // Output: Total: $500