// Set up a collator object with language-specific rules but tailored for case-insensitivity and accent insensitivity\n$collator = new Collator('en_US'); // 'en_US' is generally used, but 'root' can also be appropriate for sorting across languages\n$collator->setStrength(Collator::PRIMARY); // This ensures that the collation is case and accent insensitive\n\n// Example strings to compare\n$string1 = "café";\n$string2 = "Café";\n\n// Compare the strings using the collator\n$result = $collator->compare($string1, $string2);\n\nif ($result < 0) {\n    echo "'$string1' is less than '$string2'";\n} elseif ($result == 0) {\n    echo "'$string1' is equal to '$string2'";\n} else {\n    echo "'$string1' is greater than '$string2'";\n}