<?php\n\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database_name";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT tb1.f1, tb1.f2, tb2.f1 FROM tb1, tb2";\n$result = $conn->query($sql);\n\n$output = [];\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        // Assuming the order of fields selected does not change\n        $output[] = [\n            'tb1' => ['f1' => $row['f1'], 'f2' => $row['f2']],\n            'tb2' => ['f1' => $row['tb2.f1']]\n        ];\n    }\n} else {\n    echo "0 results";\n}\n$conn->close();\n\n// Now you can access the data like\nforeach ($output as $item) {\n    echo "tb1 f1: " . $item['tb1']['f1'] . ", tb1 f2: " . $item['tb1']['f2'] . ", tb2 f1: " . $item['tb2']['f1'];\n}\n?>