<?php\nfunction obfuscateEmail($email) {\n    $parts = explode('@', $email);\n    if (count($parts) == 2) {\n        list($localPart, $domain) = $parts;\n        // Convert characters to HTML entities or random numbers\n        $obfuscatedEmail = '';\n        for ($i = 0; $i < strlen($localPart); $i++) {\n            obfuscatedEmail .= '&#' . ord($localPart[$i]) . ';';\n        }\n        $domainObfuscated = str_replace('@', '&#64;', " @{$domain}");\n        // Split domain into chunks to confuse bots\n        $obfuscatedDomainParts = array_chunk(str_split(substr($domainObfuscated, 1)), rand(2, 3));\n        $encodedDomain = '';\n        foreach ($obfuscatedDomainParts as $part) {\n            $encodedDomain .= '<span>' . implode('', $part) . ' </span>';\n        }\n        return "<script type=\"text/javascript\">/*<![CDATA[*/document.write('".($obfuscatedEmail).$encodedDomain."');</script>";\n    }\n    return $email; // If the email is not properly formatted, return it as is\n}\n\n// Example usage\n$userEmail = "example@example.com";\necho obfuscateEmail($userEmail);\n?>