// Assume $dom is your DOMDocument object and $identityElement is the DomElement you want to rename.\n$identityElement = $dom->getElementsByTagName('identity')->item(0);\n\nif ($identityElement) {\n    // Create a new element with the desired tag name 'person'\n    $newElement = $dom->createElement('person');\n\n    // Copy all attributes\n    foreach ($identityElement->attributes as $attribute) {\n        $newElement->setAttribute($attribute->name, $attribute->value);\n    }\n\n    // Append all child nodes to the new element\n    while ($childNode = $identityElement->firstChild) {\n        $newElement->appendChild($childNode);\n    }\n\n    // Replace the old element with the new one\n    $identityElement->parentNode->replaceChild($newElement, $identityElement);\n}\n\n// Save or further process your DOMDocument object as needed.