// Assuming $rows contains your fetched data from MySQL\n$events = []; // Initialize an array to hold your events\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $event = [\n        'title' => htmlspecialchars($row['event_title']),\n        'start' => $row['event_start'],\n        'end' => $row['event_end'], // Assuming you have end dates\n        'url' => htmlspecialchars($row['event_url']) // Optional: URL for the event\n    ];\n    $events[] = $event; // Add each event to the events array\n}\n\n// Encode the entire array of events as JSON and output it\necho json_encode($events);