<?php\n\nfunction hexToByte($hexString) {\n    $bytes = [];\n    for ($i = 0; $i < strlen($hexString); $i += 2) {\n        $bytes[] = chr(hexdec(substr($hexString, $i, 2)));\n    }\n    return implode('', $bytes);\n}\n\nfunction hashPassword($password) {\n    $encryptionKey = "AE09F72B007CAAB5";\n    $keyBytes = hexToByte($encryptionKey);\n    \n    // Use mb_convert_encoding to convert the password to UTF-16LE\n    $passwordBytes = mb_convert_encoding($password, 'UTF-16LE', 'UTF-8');\n    \n    // Hash using HMAC-SHA1\n    $hash = hash_hmac('sha1', $passwordBytes, $keyBytes, true);\n    \n    // Encode the result in Base64\n    return base64_encode($hash);\n}\n\n// Example usage\n$password = "pa55w0rd";\n$hashedPassword = hashPassword($password);\n\necho $hashedPassword;  // Should output: oK9NOVhpTkxLoLfvh1430SFb5gw=