$tplContent = '{foreach from=$tabMethodTest item=entry}\n    /** \n     * @todo Implement test{$entry.name}().\n     */\n    public function test{$entry.name}() {\n        $this->markTestIncomplete("This test has not been implemented yet.");\n    }\n{/foreach}';\n\n// Extract foreach loop content\npreg_match('/\{foreach\s+from=\$(.*?)\s+item=(.*?)\}(.*?)\{\/foreach\}/is', $tplContent, $matches);\n\nif (!empty($matches)) {\n    $arrayVariable = trim($matches[1], '$'); // e.g., "tabMethodTest"\n    $itemName = trim($matches[2]); // e.g., "entry"\n    $templateContent = $matches[3];\n\n    // Example array data (normally this would come from your application)\n    $data = [\n        ['name' => 'ExampleOne'],\n        ['name' => 'ExampleTwo'],\n        ['name' => 'ExampleThree']\n    ];\n\n    foreach ($data as $itemData) {\n        // Replace '{$itemName.key}' with the actual value\n        $output = preg_replace_callback('/\{\$' . preg_quote($itemName, '/') . '\.([^\}]+)\}/', function($matches) use ($itemData) {\n            return htmlspecialchars(isset($itemData[$matches[1]]) ? $itemData[$matches[1]] : '', ENT_QUOTES);\n        }, $templateContent);\n\n        echo $output;\n    }\n}