<?php\n// Get the request URI from query parameter set in .htaccess\n$requestUri = $_GET['path'] ?? '';\n\n// Split the request URI into an array of path parts\n$uriParts = explode('/', trim($requestUri, '/'));\n\n// Determine controller and action based on $uriParts\n$controllerName = ucfirst((isset($uriParts[0]) && !empty($uriParts[0])) ? $uriParts[0] : 'Index') . 'Controller';\n$actionName = (isset($uriParts[1]) && !empty($uriParts[1])) ? $uriParts[1] : 'index';\n\n// Construct paths to the controller file\n$controllerFile = "controllers/{$controllerName}.php";\n\ntry {\n    // Check if the controller exists and include it\n    if (!file_exists($controllerFile)) {\n        throw new Exception("Not found");\n    }\n    require $controllerFile;\n\n    // Initialize the controller\n    /** @var Controller $controller */\n    $controller = new $controllerName();\n\n    // Call the action method if it exists, otherwise show 404\n    if (!method_exists($controller, $actionName)) {\n        throw new Exception("Not found");\n    }\n\n    call_user_func([$controller, $actionName]);\n\n} catch (Exception $e) {\n    http_response_code(404);\n    echo "404 Not Found";\n}