<?php\n// Your RSS feed credentials\n$username = 'your_username';\n$password = 'your_password';\n\n// Check for HTTP Authorization header\nif (!isset($_SERVER['PHP_AUTH_USER']) || !isset($_SERVER['PHP_AUTH_PW'])\n    || $_SERVER['PHP_AUTH_USER'] != $username || $_SERVER['PHP_AUTH_PW'] != $password) {\n    // Set headers to prompt for username and password\n    header('WWW-Authenticate: Basic realm="Private RSS Feed"');\n    header('HTTP/1.0 401 Unauthorized');\n    echo 'Authentication Required.';\n    exit;\n}\n\n// Your existing code to generate the RSS feed goes here\nheader("Content-Type: application/rss+xml; charset=UTF-8");\necho '<?xml version="1.0" encoding="UTF-8"?>';\n?>\n<rss version="2.0">\n  <channel>\n    <title>My Intranet RSS Feed</title>\n    <link>http://example.com/intranet</link>\n    <description>RSS feed for new documents in the intranet system</description>\n    <!-- Add your items here -->\n  </channel>\n</rss>