<?php\nsession_start(); // Start the session\n\nif (isset($_POST['Add'])) {\n    // Calculate and store values in session variables\n    $name = $_POST['name'];\n    $base = $_POST['base'];\n    $location = $_POST['location'];\n\n    $_SESSION['name'] = $name;\n    $_SESSION['base'] = $base;\n    $_SESSION['location'] = $location;\n\n    // Generate another form with a submit button\n    ?>\n    <form method="post" action="">\n        <!-- other inputs if any -->\n        <input type="submit" name="StoreInDB" value="Add to Database">\n    </form>\n    <?php\n}\n\nif (isset($_POST['StoreInDB'])) {\n    // Retrieve values from session variables\n    $name = $_SESSION['name'];\n    $base = $_SESSION['base'];\n    $location = $_SESSION['location'];\n\n    // Add data to the database here using $name, $base, and $location\n\n    // Optionally, unset session variables if they are no longer needed\n    unset($_SESSION['name']);\n    unset($_SESSION['base']);\n    unset($_SESSION['location']);\n}\n?>