function Validate() {\n    if (!empty($_POST["From"]) && !empty($_POST["Body"])) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\nfunction Postback() {\n    if (isset($_POST["Return"])) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\nfunction Email() {\n    global $Valid; // Declare $Valid as global to access the variable outside this function\n    if (isset($Valid)) {\n        return $Valid !== 0; // Simplified return statement\n    } else {\n        if (Postback()) {\n            if (Validate()) {\n                $Valid = 1;\n                mail('example@example.com', 'Subject', $_POST['Body']); // Ensure to replace with actual email logic\n            } else {\n                $Valid = 0;\n            }\n        }\n    }\n}\n\n// Assuming you want to check and print status after Email() is called twice\nEmail();\nif (isset($Valid)) {\n    echo $Valid ? "Confirmation: Email sent successfully." : "Warning: Email failed to send.";\n} else {\n    Email();\n    if (isset($Valid)) {\n        echo $Valid ? "Confirmation: Email sent successfully." : "Warning: Email failed to send.";\n    }\n}