// Start with an array of your current GET variables\n$currentParams = $_GET;\n\n// Add or update the 'bar' parameter\n$currentParams['bar'] = 'xyz';\n\n// Build the query string from the parameters array\n$queryString = http_build_query($currentParams);\n\n// Construct the new URL\n$newUrl = "example.com/?" . $queryString;\n\n// Use $newUrl as needed (e.g., in a hyperlink or redirect)\necho '<a href="' . htmlspecialchars($newUrl) . '">Click me</a>';