/**\n * Implements hook_aggregator_item_insert().\n */\nfunction aggregator_custom_aggregator_item_insert(stdClass $item) {\n  // Convert item body to use 'aggregator_custom' input format.\n  $item->teaser = check_markup($item->teaser, 'aggregator_custom');\n  $item->body = check_markup($item->body, 'aggregator_custom');\n\n  // Save the updated item.\n  database_query("UPDATE {aggregator_item} SET teaser = %s, body = %s WHERE iid = %d", $item->teaser, $item->body, $item->iid);\n}