$values = mysql_query("SELECT lname, fname, email, dtelephone, etelephone, contactwhen, thursday, \nfriday, saturday, sunday, monday, comments FROM volunteers_2009 WHERE venue_id = $venue_id");\n\n$csv_output = "";\n\nwhile ($rowr = mysql_fetch_row($values)) {\n    for ($j=0;$j<count($rowr);$j++) {\n        // Check if the field contains a comma or double quote, and enclose it in double quotes\n        if (strpos($rowr[$j], ',') !== false || strpos($rowr[$j], '"') !== false) {\n            $rowr[$j] = '"' . str_replace('"', '""', $rowr[$j]) . '"';\n        }\n        $csv_output .= $rowr[$j];\n        if ($j < count($rowr) - 1) {\n            $csv_output .= ',';\n        }\n    }\n    $csv_output .= "\n";\n}