<?php\n// Example date from your database\n$dateFromDB = '2024-04-01'; // This should be in y-m-d format\n\n// Create DateTime objects for the current date and the date from the database\n$currentDate = new DateTime();\n$dbDate = new DateTime($dateFromDB);\n\n// Calculate the difference between the two dates\n$interval = $currentDate->diff($dbDate);\n\n// Check if the difference is greater than 13 days\nif ($interval->days > 13) {\n    echo "The date is more than 13 days ago.";\n} else {\n    echo "The date is within 13 days.";\n}\n?>