// Retrieve the config from Zend_Registry\n$config = Zend_Registry::get('config');\n\n// Check if 'domain' exists and unset it\nif (isset($config->domain)) {\n    unset($config->domain);\n}\n\n// Set the modified config back into Zend_Registry\nZend_Registry::set('config', $config);\n\n// Now, when you call getDomainFromConfig(), it should throw an exception\ntry {\n    $this->getDomainFromConfig();\n} catch (Exception $e) {\n    echo "Caught an exception: " . $e->getMessage(); // This should output your expected error message.\n}