<?php\ntry {\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare a statement with a scrollable cursor\n    $stmt = $pdo->prepare("SELECT * FROM your_table", [PDO::ATTR_CURSOR => PDO::CURSOR_SCROLL]);\n\n    // Execute the statement\n    $stmt->execute();\n\n    // Move to the third row and fetch it\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_ABS, 2);\n    print_r($row);\n\n    // Move back one row from the current position and fetch it\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_PRIOR);\n    print_r($row);\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>