<?php\n// Database connection parameters\n$host = 'localhost';\n$db   = 'your_database';\n$user = 'your_username';\n$pass = 'your_password';\n$charset = 'utf8mb4'; // Use utf8mb4 for full Unicode support\n\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\n$dsn = "mysql:host=$host;dbname=$db;charset=$charset";\ntry {\n    $pdo = new PDO($dsn, $user, $pass, $options);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// User input from textarea\n$userInput = $_POST['textbox_input']; // Ensure you validate and sanitize inputs according to your app's requirements\n\n// Prepared statement to insert data\n$stmt = $pdo->prepare("INSERT INTO your_table (text_column) VALUES (:userInput)");\n$stmt->bindParam(':userInput', $userInput, PDO::PARAM_STR);\n$stmt->execute();\n?>