<?php\n\nfunction genKey($q, $seed) {\n    // Using SHA-256 as the hashing algorithm\n    return hash_hmac('sha256', $q, $seed);\n}\n\nfunction checkKey($p, $q, $seed) {\n    // Generate a new HMAC with the same inputs and compare it to the provided key p\n    $generatedKey = genKey($q, $seed);\n    // Use hash_equals for a timing-attack safe comparison\n    return hash_equals($p, $generatedKey);\n}\n\n// Example usage:\n$q = "some_hash_or_string"; // This could be an MD5 or SHA hash\n$seed = "random_seed_string";\n\n$p = genKey($q, $seed);\n\n// Verify if the generated key matches\n$is_valid = checkKey($p, $q, $seed); // Should return true\n\necho $is_valid ? 'Valid' : 'Invalid';\n?>