class A {\n    public $myName;\n\n    // You can add __set magic method if needed, for now I'll skip it.\n}\n\nclass B extends A {\n    public function createNewA($name) {\n        $anotherA = new A;\n        $anotherA->myName = $name;\n        return $anotherA;\n    }\n}\n\n// Usage:\n$b = new B();\n$newAInstance = $b->createNewA('stackoverflow');\necho $newAInstance->myName; // Outputs: stackoverflow