function procLogin($username, $password) {\n    // Assuming you have already established a PDO connection as $pdo\n    $stmt = $pdo->prepare('SELECT member_id, userlevel FROM members WHERE login = :username');\n    $stmt->execute(['username' => $username]);\n    \n    $row = $stmt->fetch(PDO::FETCH_ASSOC);\n    \n    if ($row && password_verify($password, $row['passwd'])) {\n        return ['member_id' => $row['member_id'], 'userlevel' => $row['userlevel']];\n    } else {\n        return false;\n    }\n}